import React from 'react';
import Board from './Board';

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.onClickHandler = this.onClickHandler.bind(this);
    this.state = {
        value: 4
    }    
  }

  onClickHandler(i) {
  }

  render() {
    return (
        <div>
            <Board onClick={this.onClickHandler} value={this.state.value} />
        </div>
    )
  }
}

export default Game;



